#!/usr/bin/env python3

#Código para contar o número de nucleotídeos em uma sequência de DNA
dna = ""

print("Há", len(dna), "nucleotídeos nessa sequência.")

print("Há", dna.count("T"), "timinas nessa sequência.")
print("Há", dna.count("A"), "adeninas nessa sequência.")
print("Há", dna.count("C"), "citoninas nessa sequência.")
print("Há", dna.count("G"), "guaninas nessa sequência.")

