#!/usr/bin/env python3

dna = ""

adenina = dna.count("A")
timina = dna.count("T")

print("Há", adenina, "adeninas e", timina, "timinas nessa sequência, totalizando", adenina+timina)

citosina = dna.count("C")
guanina = dna.count("G")

print("Há", citosina, "citosinas e", guanina, "guaninas nessa sequência, totalizando", citosina+guanina)
